import React, { Fragment, useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { login } from '../../actions/auth';

const Login = ({ login, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    login(email, password);
  };

  if (isAuthenticated) {
    return <Redirect to="/dashboard" />;
  }

  return (
    <Fragment>
      <main class="d-flex w-100">
        <div class="container d-flex flex-column">
          <div class="row vh-100">
            <div class="col-sm-10 col-md-8 col-lg-6 mx-auto d-table h-100">
              <div class="d-table-cell align-middle">

                <div class="text-center mt-4">
                  <h1 class="h2">XATP13 Ishlab chiqarish tizimi</h1>
                  <p class="lead">
                    Login va parolni terib kiring
                  </p>
                </div>

                <div class="card">
                  <div class="card-body">
                    <div class="m-sm-4">
                      <div class="text-center">
                        <img src="img/avatars/avatar.jpg" alt="Charles Hall" class="img-fluid rounded-circle" width="132"
                          height="132" />
                      </div>
                      <form>
                        <div class="mb-3">
                          <label class="form-label">Email/Email</label>
                          <input class="form-control form-control-lg" type="email" name="email"
                            placeholder="Enter your email" />
                        </div>
                        <div class="mb-3">
                          <label class="form-label">Parol</label>
                          <input class="form-control form-control-lg" type="password" name="password"
                            placeholder="Enter your password" />

                        </div>
                        <div>
                          <label class="form-check">
                            <input class="form-check-input" type="checkbox" value="remember-me" name="remember-me" checked />
                            <span class="form-check-label">
                              Eslab qolish
                            </span>
                          </label>
                        </div>
                        <div class="text-center mt-3">
                          <a href="index.html" class="btn btn-lg btn-primary">Tizimga kirish</a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>

              </div>
            </div>
          </div>
        </div>
      </main>
    </Fragment>
  );
};

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { login })(Login);
