import axios from 'axios';
import { setAlert } from './alert';
import {
  SHOW_SIDEBAR,
  HIDE_SIDEBAR,
  TOGGLE_SIDEBAR,
} from './types';


export const toggleSideBar = () => (dispatch) => {
  dispatch({
    type: SHOW,
  });
  dispatch({
    type: LOGOUT,
  });
};
